INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (185459, 'Glecaprevir', 'Cyclosporine', 'Major', 'Coadministration with cyclosporine may significantly increase the plasma concentrations of glecaprevir. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of glecaprevir by cyclosporine. Inhibition of P-glycoprotein (P-gp)- and breast cancer resistance protein (BCRP)-mediated intestinal transport and CYP450 3A4-mediated metabolism of glecaprevir may also contribute. High plasma levels of glecaprevir may increase the risk of adverse effects such as alanine aminotransferase (ALT) and bilirubin elevations.', NULL, 'Glecaprevir-pibrentasvir is not recommended for use in patients requiring stable cyclosporine dosages greater than 100 mg/day.', NULL, 'Glecaprevir-pibrentasvir is not recommended for use in patients requiring stable cyclosporine dosages greater than 100 mg/day.', 'Metabolism, Absorption', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Methotrexate, More', 'Cabotegravir, Zanamivir, Velpatasvir, Stavudine, Elbasvir, Tecovirimat, Pibrentasvir, Rimantadine, Elvitegravir, Peramivir, Emtricitabine, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185676/', '[1] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.[2] Hollander AAMJ, Vanrooij J, Lentjes EGWM, Arbouw F, Vanbree JB, Schoemaker RC, Vanes LA, Vanderwoude FJ, Cohen AF "The effect of grapefruit juice on cyclosporine and prednisone metabolism in transplant patients." Clin Ph