INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (185516, 'Glecaprevir', 'Pitavastatin', 'Moderate', 'Coadministration with glecaprevir-pibrentasvir may increase the plasma concentrations of all statins including fluvastatin and pitavastatin. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of statins by glecaprevir and pibrentasvir. Although the interaction has not been studied specifically with fluvastatin or pitavastatin, it has been reported with other statins that are also substrates of OATP1B1. High levels of HMG-CoA reductase inhibitory activity in plasma may be associated with an increased risk of musculoskeletal toxicity. Myopathy manifested as muscle pain and/or weakness associated with grossly elevated creatine kinase exceeding ten times the upper limit of normal has been reported occasionally. Rhabdomyolysis has also occurred rarely, which may be accompanied by acute renal failure secondary to myoglobinuria and may result in death.', NULL, 'Caution is advised during concomitant administration of fluvastatin or pitavastatin with glecaprevir-pibrentasvir.', NULL, 'Caution is advised during concomitant administration of fluvastatin or pitavastatin with glecaprevir-pibrentasvir. The lowest approved dosage of the statin should be used. If higher dosages are required, the lowest effective statin dosage should be prescribed based on a risk/benefit assessment.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/185733/', '[1] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Mavyret (glecaprevir-p