INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435072, 'Probenecid', 'Phenylbutyric acid', 'Major', 'Probenecid may inhibit the renal excretion of metabolites of phenylbutyrate, including phenylacetate (PAA) and phenylacetylglutamine (PAGN). PAA is associated with neurotoxic effects such as somnolence, fatigue, lightheadedness, headache, dysgeusia, hypoacusis, disorientation, memory impairment, tremor, peripheral neuropathy, and seizures.', 'DDInter', 1767317476, 'Since plasma PAA and urinary PAGN levels may be used to help guide dosing of phenylbutyrate therapy, caution is advised if probenecid is coadministered. The potential for increased risk of PAA-induced neurotoxicity should also be considered.', 'Metabolism', 'Since plasma PAA and urinary PAGN levels may be used to help guide dosing of phenylbutyrate therapy, caution is advised if probenecid is coadministered.', NULL, NULL, 'Major', 'pharmacodynamic');
