INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435074, 'Aluminum hydroxide', 'Tolevamer', 'Major', 'Systemic alkalosis has been reported when cation-exchange resins were administered orally in combination with nonabsorbable cation-donating preparations such as antacids or laxatives. The mechanism involves binding of the cation to the resin, which prevents the cation from neutralizing bicarbonate ions in the intestine and also interferes with the resin''s potassium exchange capability.', 'DDInter', 1767317476, 'Nonabsorbable calcium, magnesium, or aluminum preparations such as antacids or laxatives should not be used in patients receiving oral cation-exchange resins. Alternatively, the interaction may be avoided by administering the resin as an enema.', 'Others', 'Nonabsorbable calcium, magnesium, or aluminum preparations such as antacids or laxatives should not be used in patients receiving oral cation-exchange resins.', NULL, NULL, 'Major', 'pharmacodynamic');
