INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435118, 'Tolevamer', 'Quinapril', 'Moderate', 'INTERVAL: Administration of quinapril with some oral medications may significantly decrease their absorption and may result in subtherapeutic serum concentrations. The proposed mechanism is chelation of the drug by the magnesium hydroxide excipient in quinapril tablets.', 'DDInter', 1767317476, 'The manufacturer does not make a specific recommendation, but generally the administration of these drugs and magnesium-containing products should be separated by at least two to three hours.', 'Absorption', 'The manufacturer does not make a specific recommendation, but generally the administration of these drugs and magnesium-containing products should be separated by at least two to three hours.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435119, 'Tolevamer', 'Sorbitol', 'Major', 'The concomitant oral or rectal use of sorbitol with polystyrene sulfonate exchange resins may result in colonic necrosis. Fatalities have occurred when uremic patients received sodium polystyrene sulfonate/sorbitol enemas. The mechanism is unknown and causality has not been clearly established. Sorbitol is also present in some oral suspensions (e.g., Mobic, brand of meloxicam).', 'DDInter', 1767317476, 'Until more information is available, the concurrent use of sorbitol as a vehicle or otherwise should generally be avoided in patients receiving polystyrene sulfonate exchange resins.', 'Others', 'Until more information is available, the concurrent use of sorbitol as a vehicle or otherwise should generally be avoided in patients receiving polystyrene sulfonate exchange resins.', NULL, NULL, 'Major', 'pharmacodynamic');
