INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435126, 'Ziprasidone', 'Tolevamer', 'Major', 'Ziprasidone can cause dose-related prolongation of the QT interval. While clinical data are lacking, the coadministration of ziprasidone and agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential.', 'DDInter', 1767317476, 'Caution is advised when ziprasidone must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with ziprasidone. Patients should be advised to notify their physician if they experience possible signs of an electrolyte imbalance, such as weakness, lethargy, drowsiness, confusion, muscle pains or cramps, dizziness, nausea, vomiting, tachycardia, or an irregular heartbeat.', 'Synergism', 'Caution is advised when ziprasidone must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435127, 'Abarelix', 'Sodium sulfate', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317476, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
