INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435421, 'Penicillamine', 'Magnesium sulfate', 'Moderate', 'INTERVAL: Oral administration of aluminum, copper, iron, zinc, magnesium, and possibly other minerals such as calcium may decrease the gastrointestinal absorption of penicillamine, and vice versa. The proposed mechanism involves chelation of penicillamine to polyvalent cations, which leads to formation of a nonabsorbable complex.', 'DDInter', 1767317476, 'Mineral supplements or other products containing polyvalent cations (e.g., antacids or preparations containing antacids such as didanosine buffered tablets or pediatric oral solution) should be administered at least two hours before or two hours after the penicillamine dose. In addition, pharmacologic response to penicillamine should be monitored more closely whenever these products are added to or withdrawn from therapy, and the penicillamine dosage adjusted as necessary.', 'Absorption', 'Mineral supplements or other products containing polyvalent cations (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435422, 'Sodium sulfate', 'Pentamidine', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317476, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
