INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435554, 'Cimetidine', 'Velpatasvir', 'Moderate', 'Coadministration of velpatasvir with H2-receptor antagonists may reduce its gastrointestinal absorption. Velpatasvir exhibits pH-dependent solubility, with increased solubility at lower pH.', 'DDInter', 1767317476, 'H2-receptor antagonists may be administered simultaneously with or 12 hours apart from sofosbuvir-velpatasvir at a dose that does not exceed the equivalent of famotidine 40 mg twice daily.', 'Absorption', 'H2-receptor antagonists may be administered simultaneously with or 12 hours apart from sofosbuvir-velpatasvir at a dose that does not exceed the equivalent of famotidine 40 mg twice daily.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435555, 'Cobimetinib', 'Velpatasvir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of cobimetinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317476, 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities and rhabdomyolysis, and the cobimetinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435556, 'Colchicine', 'Velpatasvir', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may significantly increase the serum concentrations of colchicine. The mechanism involves enhanced absorption as well as reduced excretion of colchicine due to inhibition of P-gp efflux transporter in the intestine, renal proximal tubule, and liver.', 'DDInter', 1767317476, 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors. For the treatment of acute gout flares, the adjusted dosage recommended is 0.6 mg for one dose. Administration should not be repeated for at least three days.', 'Excretion', 'Due to the risk of life-threatening and fatal toxicity, patients with renal or hepatic impairment should not be given colchicine in combination with P-glycoprotein inhibitors such as cyclosporine, carvedilol, amiodarone, bepridil, quinidine, quinine, propafenone, ranolazine, spironolactone, tamoxifen, ulipristal, and some tyrosine kinase inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435557, 'Dabrafenib', 'Velpatasvir', 'Major', 'Coadministration with potent or moderate inducers of CYP450 isoenzymes may decrease the plasma concentrations of velpatasvir, which has been shown in vitro to be metabolized by CYP450 2B6, 2C8, and 3A4.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of sofosbuvir-velpatasvir with potent or moderate CYP450 inducers is not recommended.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of sofosbuvir-velpatasvir with potent or moderate CYP450 inducers is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435558, 'Daunorubicin', 'Velpatasvir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', 'DDInter', 1767317476, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435559, 'Daunorubicin (liposomal)', 'Velpatasvir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', 'DDInter', 1767317476, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
