INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435573, 'Gilteritinib', 'Velpatasvir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of gilteritinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317476, 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for adverse effects such as QT interval prolongation, pancreatitis, liver transaminase and bilirubin elevations, edema, infections and stomatitis, and the gilteritinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
