INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (186322, 'Haloperidol', 'Glycerol phenylbutyrate', 'Moderate', 'Drugs that can increase plasma ammonia levels such as corticosteroids, haloperidol, or the anticonvulsants carbamazepine, topiramate, and phenobarbital may interfere with the therapeutic effects of phenylbutyrate therapy in the management of urea cycle disorders. Corticosteroids can cause the breakdown of body protein, which may lead to increased ammonia levels. The use of haloperidol has been associated with hyperammonemia in a young child with citrullinemia, an inherited disorder of ammonia excretion.', NULL, 'Plasma ammonia levels should be closely monitored when corticosteroids, haloperidol, carbamazepine, topiramate, or phenobarbital are used in patients with urea cycle disorders receiving phenylbutyrate therapy.', NULL, 'Plasma ammonia levels should be closely monitored when corticosteroids, haloperidol, carbamazepine, topiramate, or phenobarbital are used in patients with urea cycle disorders receiving phenylbutyrate therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/186539/', '[1] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Buphenyl (sodium phenylbutyrate)." Horizon Pharma USA Inc, Northbrook, IL.', 1767365209);
