INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (186400, 'Haloperidol', 'Thiopental', 'Moderate', 'Barbiturates may decrease serum butyrophenone concentrations. The mechanism may be related to induction of the CYP450 metabolism of butyrophenones. This interaction has been reported for phenobarbital but may also occur with other barbiturates. In addition, butyrophenones may counteract the anticonvulsant effects of barbiturates by lowering the seizure threshold.', NULL, 'Caution is advised if butyrophenones are used concomitantly with barbiturates.', NULL, 'Caution is advised if butyrophenones are used concomitantly with barbiturates. Dosage adjustments as well as clinical monitoring should be considered whenever either of these agents is added to or withdrawn from therapy. Patients should be monitored for loss of seizure control, decreased neuroleptic effect, hypotension, and the development of adverse effects including CNS and respiratory depression.', 'Metabolism, Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/186617/', '[1] Prakash R, Reed RM, Bass AD "Combination of phenobarbital and haloperidol in resistant schizophrenia." J Clin Psychopharmacol 4 (1984): 362-3[2] Linnoila M, Viukari M, Vaisanen K, Auvinen J "Effect of anticonvulsants on plasma haloperidol and thioridazine levels." Am J Psychiatry 137 (1980): 819-21[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365209);
