INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (186498, 'Histrelin', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', NULL, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', NULL, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/186721/', '[1] Vrolix M, Piessens J, De Geest H "Torsades de pointes after intracoronary papaverine." Eur Heart J 12 (1991): 273-6[2] Jain A, Jenkins MG "Intracoronary electrocardiogram during torsade des pointes secondary to intracoronary papaverine." Cathet Cardiovasc Diagn 18 (1989): 255-7[3] Goto M, Sato M, Kitzazawa H, et.al "Papaverine-induced QT interval prolongation and ventricular fibrillation in a patient with a history of drug-induced QT prolongation." Intern Med 53 (2014): 1629-31[4] Talman CL, Winniford MD, Rossen JD, Simonetti I, Kienzle MG, Marcus ML "Polymorphous ventricular tachycardia: a side effect of intracoronary papaverine." J Am Coll Cardiol 15 (1990): 275-8[5] Kern MJ, Deligonul U, Serota H, Gudipati C, Buckingham T "Ventricular arrhythmia due to intracoronary papaverine: analysis of QT intervals and coronary vasodilatory reserve." Cathet Cardiovasc Diagn 19 (1990): 229-36[6] Nakayama M, Tanaka N, Sakoda K, et.al "Papaverine-induced polymorphic ventricular tachycardia during coronary flow reserve study of patients with moderate coronary artery disease." Circ J 79 (2015): 530-6[7] Inoue T, Asahi S, Takayanagi K, Morooka S, Takabatake Y "QT prolongation and possibility of ventricular arrhythmias after intracoronary papaverine." Cardiology 84 (1994): 9-13[8] Nakayama M, Saito A, Kitazawa H, et.al "Papaverine-induced polymorphic ventricular tachycardia in relation to QTU and giant T-U waves in four cases." Intern Med 51 (2012): 351-6', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (186499, 'Histrelin', 'Perphenazine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/186722/', '[1] "Product Information. Vantas (histrelin)." Endo Pharmaceuticals (formally Indevus Pharmaceuticals Inc), Lexington, MA.[2] "Product Information. Zoladex (goserelin)." Zeneca Pharmaceuticals, Wilmington, DE.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Trelstar (triptorelin)" Pharmacia and Upjohn, Kalamazoo, MI.[5] "Product Information. Firmagon (degarelix)." Ferring Pharmaceuticals Inc, Tarrytown, NY.[6] "Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc, Deerfield, IL.[7] "Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals, New York, NY.[8] "Product Information. Plenaxis (abarelix)." Praecis Pharmaceuticals Inc, Waltham, MA.', 1767365209);
