    ) VALUES (1435604, 'Velpatasvir', 'Revefenacin', 'Moderate', 'Coadministration of revefenacin with inhibitors of organic anion transporting polypeptide (OATP) 1B1 and/or 1B3 may increase systemic exposure to its active metabolite, which has been reported to be a substrate of the hepatic uptake transporters.', NULL, 'Concomitant use of revefenacin with OATP 1B1 and/or 1B3 inhibitors is not recommended.', NULL, 'Concomitant use of revefenacin with OATP 1B1 and/or 1B3 inhibitors is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
