        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1435606, 'Velpatasvir', 'Ripretinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of ripretinib and its active metabolite, DP-5439. In vitro studies indicate that ripretinib and DP-5439 are substrates of the P-gp efflux transporter.', NULL, 'Until more information is available, caution is recommended when ripretinib is used concomitantly with drugs that are inhibitors of P-gp.', NULL, 'Until more information is available, caution is recommended when ripretinib is used concomitantly with drugs that are inhibitors of P-gp. Clinical and laboratory monitoring should be considered whenever a P-gp inhibitor is added to or withdrawn from therapy with ripretinib, and the dosage adjusted as necessary. Patients should be monitored for development of adverse effects such as nausea, vomiting, constipation, diarrhea, alopecia, arthralgia, myalgia, hypertension, cardiac dysfunction, and palmar-plantar erythrodysesthesia.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
INSERT OR IGNORE INTO drug_interactions (
