        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1435611, 'Velpatasvir', 'Sodium bicarbonate', 'Moderate', 'INTERVAL: Concurrent administration of velpatasvir with drugs that elevate gastric pH such as antacids may reduce its gastrointestinal absorption. Velpatasvir exhibits pH-dependent solubility, with increased solubility at lower pH. Drugs that increase gastric pH are expected to decrease the plasma concentration of velpatasvir.', NULL, 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of sofosbuvir-velpatasvir.', NULL, 'If an antacid is required, the antacid should be taken 4 hours before or 4 hours after administration of sofosbuvir-velpatasvir. The same precaution may be applicable to other drugs with antacid effects.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
