        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1435618, 'Velpatasvir', 'Topotecan', 'Moderate', 'Coadministration with velpatasvir may increase the plasma concentrations of topotecan. Although not studied, a clinically significant pharmacokinetic interaction may be anticipated because topotecan is a substrate of P-glycoprotein (P-gp) and breast cancer resistance protein (BCRP), and velpatasvir inhibits both of these drug transporters according to the manufacturer.', NULL, 'Concomitant use of topotecan with sofosbuvir-velpatasvir is not recommended.', NULL, 'Concomitant use of topotecan with sofosbuvir-velpatasvir is not recommended.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
