INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435917, 'Insulin human (inhalation, rapid acting)', 'Tiotropium', 'Moderate', 'INTERVAL: Bronchodilators and other orally inhaled products may alter the absorption of inhaled human insulin. In nondiabetic subjects with mild asthma (n=36) and moderate asthma (n=31), administration of albuterol 30 minutes prior to inhaled human insulin resulted in a mean increase in insulin systemic exposure (AUC).', 'DDInter', 1767317476, 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended. Blood glucose should be monitored closely, and the insulin dose adjusted accordingly.', 'Absorption', 'Consistent timing of dosing of other inhaled products relative to inhaled human insulin administration is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435918, 'Ipratropium (nasal)', 'Tiotropium', 'Minor', 'Theoretically, the potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (new onset or exacerbation) when ipratropium nasal spray is used with other agents that possess anticholinergic properties (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide). However, due to the poor systemic absorption of ipratropium when administered nasally, the interaction would be unlikely at regularly recommended dosages.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1435919, 'Isoflurophate (ophthalmic)', 'Tiotropium', 'Moderate', 'Theoretically, anticholinergic agents and other agents with significant anticholinergic activity (e.g., antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, class IA antiarrhythmics especially disopyramide) may antagonize the effects of topically administered cholinergic agents such as acetylcholine, carbachol, demecarium, echothiophate, isoflurophate, physostigmine, and pilocarpine. The proposed mechanism involves opposing pharmacodynamic action on muscarinic receptor sites in ocular tissue. This interaction is sometimes desirable and is the basis for using atropine in the treatment of excessive muscarinic side effects and cholinergic crisis induced by cholinergic overdose.', 'DDInter', 1767317476, 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', 'Antagonism', 'Patients receiving long-term therapy with anticholinergic agents should be monitored for potentially diminished therapeutic (miotic) response to ophthalmic cholinergic therapy, and dosages adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
