INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436043, 'Boceprevir', 'Vardenafil', 'Major', 'Coadministration with boceprevir may significantly increase the plasma concentrations of vardenafil. The mechanism involves inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of vardenafil. The interaction has not been specifically studied with boceprevir, but has been reported with other potent CYP450 3A4 inhibitors such as HIV protease inhibitors, ketoconazole, and erythromycin.', 'DDInter', 1767317476, 'Patients treated with boceprevir should not receive more than a single 2.5 mg dose of vardenafil within a 24-hour period. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Patients treated with boceprevir should not receive more than a single 2.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436044, 'Carteolol', 'Vardenafil', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', 'DDInter', 1767317476, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
