INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436070, 'Fingolimod', 'Vardenafil', 'Major', 'Due to its significant bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of fingolimod treatment in patients receiving drugs that prolong the QT interval. Fingolimod can cause a decrease in heart rate during initiation of therapy that is apparent within an hour of the first dose and maximal at approximately 6 hours postdose in most cases, but occasionally up to 20 hours after the first dose.', 'DDInter', 1767317476, 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, close monitoring is recommended during initiation of fingolimod treatment in patients receiving concomitant drugs that can prolong the QT interval, patients with significant QT prolongation (QTc >470 msec in females or >450 msec in males), or patients with relevant risk factors for QT prolongation (e.g., hypokalemia, hypomagnesemia, congenital QT prolongation).', 'Synergism', 'Fingolimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436071, 'Flecainide', 'Vardenafil', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317476, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436072, 'Fluconazole', 'Vardenafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C9 may increase the plasma concentrations of vardenafil, which is primarily metabolized by the former and secondarily by the latter. The possibility of prolonged and/or increased pharmacologic effects of vardenafil should be considered.', 'DDInter', 1767317476, 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance. The manufacturer recommends that an initial vardenafil dosage of 2.5 to 5 mg be used in patients treated concomitantly with a potent CYP450 3A4 inhibitor such as erythromycin, itraconazole, ketoconazole, indinavir, and ritonavir.', 'Metabolism', 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance.', NULL, NULL, 'Moderate', 'pharmacodynamic');
