INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436084, 'Galantamine', 'Vardenafil', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', 'DDInter', 1767317476, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436085, 'Gatifloxacin', 'Vardenafil', 'Major', 'Certain quinolones, including gatifloxacin and moxifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317476, 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
