INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436127, 'Isoxsuprine', 'Vardenafil', 'Moderate', 'Phosphodiesterase-5 (PDE5) inhibitors may potentiate the blood pressure-lowering effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can lead to peripheral vasodilation.', 'DDInter', 1767317476, 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents. Patients receiving the combination should be advised to avoid rising abruptly from a sitting or recumbent position, especially following treatment initiation or a dosage increase, and to contact their doctor if they experience symptoms of hypotension such as dizziness, lightheadedness, fainting, or tachycardia.', 'Synergism', 'Caution is advised if PDE5 inhibitors are prescribed in combination with antihypertensive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436128, 'Itraconazole', 'Vardenafil', 'Major', 'Coadministration with the azole antifungal agents, ketoconazole and itraconazole, may significantly increase the plasma concentrations of vardenafil. The mechanism is azole inhibition of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of vardenafil.', 'DDInter', 1767317476, 'Patients treated with ketoconazole or itraconazole at a dosage of 200 mg/day should not receive more than a single 5 mg dose of vardenafil within a 24-hour period. Since higher dosages of the azole antifungal agents may result in higher increases in vardenafil plasma concentrations, a single 2.5 mg dose of vardenafil should not be exceeded within a 24-hour period when used in combination with ketoconazole or itraconazole 400 mg/day. Some authorities recommend avoiding concomitant use of vardenafil during and for 2 weeks after treatment with itraconazole. Patients should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Patients treated with ketoconazole or itraconazole at a dosage of 200 mg/day should not receive more than a single 5 mg dose of vardenafil within a 24-hour period.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436129, 'Ivabradine', 'Vardenafil', 'Major', 'Due to its bradycardic effects, the risk of QT prolongation and torsade de pointes arrhythmia may be increased when ivabradine is used with drugs that prolong the QT interval.', 'DDInter', 1767317476, 'Coadministration of ivabradine with drugs that can prolong the QT interval should generally be avoided. Caution and cardiac monitoring are recommended if concomitant use is required.', 'Synergism', 'Coadministration of ivabradine with drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
