INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (187383, 'Idelalisib', 'Lactobacillus acidophilus', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187607/', '[1] Borriello SP, Hammes WP, Holzapfel W, et.al "Safety of probiotics that contain lactobacilli or bifidobacteria." Clin Infect Dis 36 (2003): 775-80[2] Salminen MK, Tynkkynen S, Rautelin H, et al. "Lactobacillus bacteremia during a rapid increase in probiotic use of Lactobacillus rhamnosus GG in Finland." Clin Infect Dis 35 (2002): 1155-60[3] Schlegel L, Lemerle S, Geslin P "Lactobacillus species as opportunistic pathogens in immunocompromised patients." Eur J Clin Microbiol Infect Dis 17 (1998): 887-8[4] Salminen MK, Rautelin H, Tynkkynen S, et al. "Lactobacillus bacteremia, clinical significance, and patient outcome, with special focus on probiotic L. rhamnosus GG." Clin Infect Dis 38 (2004): 62-9[5] Husni RN, Gordon SM, Washington JA, Longworth DL "Lactobacillus bacteremia and endocarditis: review of 45 cases." Clin Infect Dis 25 (1997): 1048-55[6] Boyle RJ, Robins-Browne RM, Tang ML "Probiotic use in clinical practice: what are the risks?" Am J Clin Nutr 83 (2006): 1256-64[7] Lolis N, Veldekis D, Moraitou H, et.al "Saccharomyces boulardii fungaemia in an intensive care unit patient treated with caspofungin." Crit Care 12 (2008): epub[8] Rautio M, Jousimies-Somer H, Kauma H, et al. "Liver abscess due to a Lactobacillus rhamnosus strain indistinguishable from L. rhamnosus strain GG." Clin Infect Dis 28 (1999): 1159-60[9] Saxelin M, Chuang NH, Chassy B, et.al "Lactobacilli and bacteremia in southern Finland, 1989-1992" Clin Infect Dis 22 (1996): 564-6[10] Oggioni MR, Pozzi G, Valensin PE, Galieni P, Bigazzi C "Recurrent septicemia in an immunocompromised patient due to probiotic strains of Bacillus subtilis." J Clin Microbiol 36 (1998): 325-6[11] Mackay AD, Taylor MB, Kibbler CC, Hamilton-Miller JM "Lactobacillus endocarditis caused by a probiotic organism." Clin Microbiol Infect 5 (1999): 290-2', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (187384, 'Idelalisib', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', NULL, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil). Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine). Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187608/', '[1] "Product Information. Chirocaine (levobupivacaine)" Organon, West Orange, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363026);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (187385, 'Idelalisib', 'Levacetylmethadol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and duration of action of levomethadyl acetate, which is a substrate of the isoenzyme. High plasma levels of levomethadyl acetate may increase the risk of QT interval prolongation, which is thought to be associated with serious cardiac events such as ventricular arrhythmias (e.g., torsade de pointes, ventricular tachycardia), cardiac arrest, and sudden death. Cases of QT prolongation and torsade de pointes have been reported with levomethadyl acetate alone during postmarketing use.', NULL, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', NULL, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/187609/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[3] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767363026);
