    ) VALUES (1436427, 'Sufentanil', 'Levacetylmethadol', 'Moderate', 'Potent narcotics, when used as an adjuvant in patients receiving alfentanil, remifentanil, or sufentanil for anesthesia or conscious sedation, may further decrease cardiac output and respiratory drive.', NULL, 'The dosage of alfentanil, remifentanil, or sufentanil may need to be decreased in patients who have received large doses of narcotics.', NULL, 'The dosage of alfentanil, remifentanil, or sufentanil may need to be decreased in patients who have received large doses of narcotics.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
