        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1436490, 'Sufentanil', 'Oxazepam', 'Major', 'Concomitant use of opioids with benzodiazepines may result in profound sedation, respiratory depression, coma, and death. The use of benzodiazepines with sufentanil during induction has been reported to decrease mean arterial pressure and systemic vascular resistance. The mechanism appears to be an indirect effect on the autonomic nervous system.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect. Patients receiving sufentanil with benzodiazepines should be monitored closely for signs and symptoms of respiratory depression, altered hemodynamics, and hypotension.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
