INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436559, 'Sufentanil', 'Sotalol', 'Moderate', 'In patients receiving chronic beta-blocker therapy, the use of sufentanil may lead to a higher degree of bradycardia and hypotension. One study has shown that induction and maintenance of anesthesia requires lower doses of sufentanil in patients receiving beta-blockers.', 'DDInter', 1767317476, 'Patients should be carefully observed for clinically significant alterations in hemodynamics.', 'Synergism', 'Patients should be carefully observed for clinically significant alterations in hemodynamics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
