INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436626, 'Creatine', 'Trimethoprim', 'Minor', 'Cimetidine, trimethoprim, and probenecid are all known to interfere with the kidney''s secretion of creatinine. Administration of creatine supplements to patients on these medications may lead to an increase in the serum level of these drugs and/or of creatinine. The physician should be aware that these increased serum levels may occur, and dosages may need to be adjusted or creatine supplementation may need to be discontinued as necessary.', 'DDInter', 1767317476, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436627, 'Dapsone', 'Trimethoprim', 'Moderate', 'Serum levels of both trimethoprim and dapsone may be increased during coadministration. The mechanism is unknown. Steady state levels are reached within approximately one week.', 'DDInter', 1767317476, 'When these agents are coadministered, monitoring for methemoglobinemia may be important, especially in patients with respiratory insufficiency, anemia, or cardiovascular disease.', 'Others', 'When these agents are coadministered, monitoring for methemoglobinemia may be important, especially in patients with respiratory insufficiency, anemia, or cardiovascular disease.', NULL, NULL, 'Moderate', 'pharmacodynamic');
