INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436674, 'Indinavir', 'Trimethoprim', 'Minor', 'When indinavir was given concomitantly with trimethoprim/sulfamethoxazole DS (TMP/SMX 160/800 mg) for one week, a 19% increase was observed in the area under the plasma concentration-time curve of trimethoprim. The plasma concentrations of sulfamethoxazole and indinavir were not affected. This interaction is unlikely to be of clinical importance, although it may be appropriate to monitor the patient for trimethoprim-related toxicities.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
