INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436693, 'Irbesartan', 'Trimethoprim', 'Major', 'The use of trimethoprim in combination with other potassium-sparing drugs or potassium salts may increase the risk of hyperkalemia. Trimethoprim inhibits sodium reabsorption and potassium excretion by blocking sodium channels in the renal distal tubules.', 'DDInter', 1767317476, 'Serum potassium and sodium levels as well as renal function should be closely monitored during coadministration of trimethoprim with other potassium-sparing drugs or potassium salts, particularly in patients receiving high-dose or long-term trimethoprim treatment and in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. A dosage reduction of trimethoprim is recommended in renal dysfunction (50% reduction for CrCl between 15 and 30 mL/min). Patients should be given dietary counseling to avoid excessive intake of potassium-rich foods and salt substitutes, and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat. Trimethoprim should be discontinued if hyperkalemia occurs.', 'Excretion', 'Serum potassium and sodium levels as well as renal function should be closely monitored during coadministration of trimethoprim with other potassium-sparing drugs or potassium salts, particularly in patients receiving high-dose or long-term trimethoprim treatment and in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436694, 'Sulfamethoxazole', 'Ivosidenib', 'Minor', 'Limited data suggest that sulfamethoxazole-trimethoprim (SMX-TMP) may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with SMX-TMP intravenously. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
