INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436696, 'Trimethoprim', 'Lactobacillus acidophilus', 'Moderate', 'INTERVAL: Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic. It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations. However, the clinical significance of the interaction has not been established.', 'DDInter', 1767317476, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Others', 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436697, 'Trimethoprim', 'Lamotrigine', 'Minor', 'Lamotrigine is a weak inhibitor of dihydrofolate reductase. Theoretically, coadministration with other dihydrofolate reductase inhibitors may have additive effects on rapidly proliferating tissues such as bone marrow, possibly increasing the risk of megaloblastic anemia and other hematologic adverse effects. However, a clinically significant interaction has not been reported. During prolonged human dosing, lamotrigine did not induce significant changes in the hemoglobin concentration, mean corpuscular volume, or serum or red blood cell folate concentrations for up to one year. No significant effects on red blood cell folate concentration have been observed for up to five years.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
