INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436717, 'Trimethoprim', 'Mephenytoin', 'Moderate', 'Trimethoprim may inhibit the CYP450 hepatic metabolism of hydantoins. Serum hydantoin levels and risk of toxicity may be increased.', 'DDInter', 1767317476, 'Monitoring for clinical and laboratory evidence of hydantoin toxicity is recommended. Dose adjustments may be necessary. Patients should be advised to notify their physician if they experience symptoms of hydantoin toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia).', 'Metabolism', 'Monitoring for clinical and laboratory evidence of hydantoin toxicity is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436718, 'Trimethoprim', 'Mercaptopurine', 'Moderate', 'Even though the combination is used in clinical practice, the risk of bone marrow suppression may be increased when azathioprine or mercaptopurine is coadministered with trimethoprim or sulfamethoxazole-trimethoprim (co-trimoxazole). The risk may be increased when concurrent therapy is longer than 3 weeks. Thrombocytopenia and neutropenia have been reported; however, data have been conflicting. The mechanism is unknown, but may be related to additive myelosuppressive effects and hemodialysis-induced folate depletion.', 'DDInter', 1767317476, 'Close clinical and laboratory monitoring for the development of hematologic adverse effects is recommended both during and after discontinuation of therapy. Dosage adjustment of azathioprine or mercaptopurine may be considered if an interaction is suspected. Patients should be advised to promptly report symptoms such as chills, fever, malaise, sore throat, mouth sores, unusual fatigue, bruising, or bleeding.', 'Synergism', 'Close clinical and laboratory monitoring for the development of hematologic adverse effects is recommended both during and after discontinuation of therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
