INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436767, 'Trimethoprim', 'Potassium acetate', 'Major', 'The use of trimethoprim in combination with other potassium-sparing drugs or potassium salts may increase the risk of hyperkalemia. Trimethoprim inhibits sodium reabsorption and potassium excretion by blocking sodium channels in the renal distal tubules.', 'DDInter', 1767317476, 'Serum potassium and sodium levels as well as renal function should be closely monitored during coadministration of trimethoprim with other potassium-sparing drugs or potassium salts, particularly in patients receiving high-dose or long-term trimethoprim treatment and in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. A dosage reduction of trimethoprim is recommended in renal dysfunction (50% reduction for CrCl between 15 and 30 mL/min). Patients should be given dietary counseling to avoid excessive intake of potassium-rich foods and salt substitutes, and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat. Trimethoprim should be discontinued if hyperkalemia occurs.', 'Excretion', 'Serum potassium and sodium levels as well as renal function should be closely monitored during coadministration of trimethoprim with other potassium-sparing drugs or potassium salts, particularly in patients receiving high-dose or long-term trimethoprim treatment and in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436768, 'Aminobenzoic acid', 'Sulfamethoxazole', 'Major', 'Potassium aminobenzoate may inactivate antibacterial sulfonamides. Sulfonamides are structural analogues and competitive inhibitors of para-aminobenzoic acid, which is required by bacterial folic acid synthesis. Therefore, sufficient concentrations of potassium para-aminobenzoate may antagonize the antibacterial effects of sulfonamides.', 'DDInter', 1767317476, 'The manufacturer considers concomitant use contraindicated.', 'Antagonism', 'The manufacturer considers concomitant use contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
