INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1436773, 'Trimethoprim', 'Pralatrexate', 'Moderate', 'Coadministration with drugs that are subject to substantial renal clearance such as trimethoprim/sulfamethoxazole and nonsteroidal anti-inflammatory drugs (NSAIDs) may result in delayed clearance of pralatrexate. The mechanism is competitive inhibition of renal excretion, as approximately 34% of pralatrexate is eliminated unchanged by the kidney.', 'DDInter', 1767317476, 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs. Complete blood cell counts as well as renal and hepatic function should be closely monitored. Treatment interruption or dose reduction of pralatrexate may be required if toxicities such as mucositis, myelosuppression, or liver enzyme abnormalities develop. Patients should be advised to contact their physician if they develop oral ulcers, painful swallowing, vomiting, diarrhea, and/or signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, or signs of infection such as fever, chills, sore throat, body aches, and other influenza-like symptoms.', 'Excretion', 'Caution is advised if pralatrexate is prescribed in combination with trimethoprim/sulfamethoxazole or NSAIDs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
