        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1436848, 'Zalcitabine', 'Trimethoprim', 'Moderate', 'Trimethoprim may increase plasma concentrations and decrease clearance of zalcitabine. The mechanism may be inhibition of renal tubular secretion of zalcitabine. The clinical significance is unknown.', NULL, 'Close clinical monitoring for evidence of zalcitabine toxicity (peripheral neuropathy, pancreatitis, lactic acidosis, hepatotoxicity) is recommended when these drugs are used together.', NULL, 'Close clinical monitoring for evidence of zalcitabine toxicity (peripheral neuropathy, pancreatitis, lactic acidosis, hepatotoxicity) is recommended when these drugs are used together. In addition, a reduction in zalcitabine dosage may be warranted. Patients should be advised to promptly notify their doctor if they experience numbness, tingling, severe nausea and vomiting, diarrhea, abdominal pain, severe fatigue, or jaundice.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
