        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1436894, 'Sodium bicarbonate', 'Naproxen', 'Minor', 'Antacids and some oral aluminum, calcium, or magnesium containing preparations may decrease the gastrointestinal absorption of enteric-coated naproxen, which requires an acidic environment for dissolution. The proposed mechanism is an increase in gastric pH (i.e. decreased gastric acidity) produced by antacids. In patients treated with antacids, the possibility of a reduced or subtherapeutic response to enteric-coated naproxen should be considered. Concomitant use of these drugs is generally not recommended. Aluminum hydroxide and some magnesium-containing antacids have also been reported to reduce the absorption of regular naproxen. The interaction may be minimized by separating the times of administration by at least 2 hours.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
