    ) VALUES (1436895, 'Sucralfate', 'Naproxen', 'Minor', 'Coadministration of naproxen with cholestyramine or sucralfate may delay the absorption of naproxen but not the extent of absorption. The clinical significance is unknown.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
