        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1436955, 'Tedizolid', 'Artesunate', 'Moderate', 'Coadministration with oral tedizolid may increase the plasma concentrations and the risk of adverse effects of orally administered drugs that are substrates of the breast cancer resistance protein (BCRP) transporter. The proposed mechanism is decreased clearance due to inhibition of BCRP-mediated intestinal efflux by tedizolid.', NULL, 'Consider temporarily interrupting treatment with the BCRP substrate during treatment with tedizolid, especially for BCRP substrates with a narrow therapeutic index.', NULL, 'Consider temporarily interrupting treatment with the BCRP substrate during treatment with tedizolid, especially for BCRP substrates with a narrow therapeutic index. If coadministration cannot be avoided, monitor for adverse reactions related to concomitantly administered BCRP substrate.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
