INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437209, 'Amikacin (liposome)', 'Ticarcillin', 'Moderate', 'INTERVAL: Ticarcillin may inactivate aminoglycosides in vivo and in vitro. The mechanism is complexation with the aminoglycoside. Typically, this effect is significant only in patients with renal failure or if the agents are mixed in the same IV container or line. Lower serum aminoglycoside levels may be seen, and dosage adjustments may be necessary.', 'DDInter', 1767317476, 'Ticarcillin and aminoglycosides should be administered separately during combined therapy.', 'Others', 'Ticarcillin and aminoglycosides should be administered separately during combined therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437210, 'Anisindione', 'Ticarcillin', 'Moderate', 'Penicillins may occasionally potentiate the risk of bleeding in patients treated with oral anticoagulants. The exact mechanism of interaction is unknown but may involve penicillin inhibition of platelet aggregation.', 'DDInter', 1767317476, 'Caution is recommended if a penicillin is prescribed during oral anticoagulant therapy, especially in the elderly and patients with uremia or hepatic impairment. The INR should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of penicillin therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Caution is recommended if a penicillin is prescribed during oral anticoagulant therapy, especially in the elderly and patients with uremia or hepatic impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437211, 'Demeclocycline', 'Ticarcillin', 'Moderate', 'Tetracyclines may reduce the effect of penicillins by inhibiting cellular protein synthesis which is necessary for cell wall synthesis inhibition by penicillins. Antagonism is more likely when low doses of either agent are administered. Therapeutic failure may result.', 'DDInter', 1767317476, 'This combination should be avoided if possible.', 'Antagonism', 'This combination should be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
