INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437226, 'Iodide I-123', 'Ticarcillin', 'Moderate', 'INTERVAL: Certain drugs may affect the pharmacokinetics, pharmacodynamics, and/or diagnostic results of radioiodides.', 'DDInter', 1767317476, 'Antithyroid agents such as carbimazole or propylthiouracil should generally be withheld for 1 week before administration of sodium iodide I-131 or I-123, and for a few days afterward. Salicylates, steroids, anticoagulants, antihistamines, antiparasitics, penicillins, sulfonamides, tolbutamide, thiopental, and nitroprusside should generally be withheld for 1 week. Amiodarone, lithium, and benzodiazepines should be withheld for at least 4 weeks.', 'Others', 'Antithyroid agents such as carbimazole or propylthiouracil should generally be withheld for 1 week before administration of sodium iodide I-131 or I-123, and for a few days afterward.', NULL, NULL, 'Moderate', 'pharmacodynamic');
