INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (189124, 'Bedaquiline', 'Lomefloxacin', 'Major', 'Bedaquiline can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if bedaquiline is used in combination with other drugs that can prolong the QT interval. ECG and serum electrolytes, including potassium, magnesium and calcium, should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189351/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (189125, 'Bedaquiline', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189352/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (189126, 'Bedaquiline', 'Lonafarnib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of bedaquiline, which is primarily metabolized by CYP450 3A4 to its less active N-monodesmethyl metabolite, M2.', NULL, 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk.', NULL, 'The concomitant use of potent CYP450 3A4 inhibitors such as clarithromycin, cobicistat, conivaptan, delavirdine, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents given systemically for more than 14 consecutive days should generally be avoided during treatment with bedaquiline unless the benefit is anticipated to outweigh the risk. Appropriate clinical monitoring for bedaquiline-related adverse reactions is recommended if coadministration is required. ECG, serum electrolytes (potassium, magnesium, calcium), and liver function tests (ALT, AST, alkaline phosphatase, bilirubin) should be monitored before starting bedaquiline therapy and periodically during treatment in accordance with the product labeling. Hypokalemia, hypomagnesemia, and hypocalcemia must be corrected prior to bedaquiline administration. Bedaquiline should be interrupted in patients who develop clinically significant ventricular arrhythmia or a QTcF interval greater than 500 msec confirmed by repeat ECG. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189353/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.', 1767365209);
