INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (189314, 'Capsicum', 'Tositumomab (I-131)', 'Minor', 'Theoretically, capsicum may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. Animal and limited human data suggest that capsicum may increase fibrinolytic activity, lower plasma fibrinogen levels, increase plasma antithrombin III levels, inhibit platelet aggregation, and prolong bleeding time. In patients who have used capsicum extensively prior to receiving anticoagulation, antiplatelet, or thrombolytic therapy, the potential for an interaction should be considered. Clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189541/', '[1] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000): 1221-7; quiz 1228-30', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (189315, 'Chamomile', 'Tositumomab (I-131)', 'Minor', 'Some herbs such as chamomile and fenugreek have coumarin constituents. Theoretically, ingesting large quantities of these herbs may potentiate the risk of bleeding in patients treated with agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. However, their effects on the coagulation system have not been studied, and bleeding complications have not been reported in humans. Patients should consult a healthcare provider before taking any herbal or alternative medicine. In patients who have used chamomile or fenugreek extensively prior to receiving anticoagulation, antiplatelet or thrombolytic therapy, the potential for an interaction should be considered. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189542/', '[1] Heck AM, DeWitt BA, Lukes AL "Potential interactions between alternative therapies and warfarin." Am J Health Syst Pharm 57 (2000): 1221-7; quiz 1228-30[2] Miller LG "Herbal medicinals: selected clinical considerations focusing on known or potential drug-herb interactions." Arch Intern Med 158 (1998): 2200-11[3] Lambert JP, Cormier J "Potential interaction between warfarin and boldo-fenugreek." Pharmacotherapy 21 (2001): 509-12', 1767365209);
