INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (189749, 'Chloramphenicol (otic)', 'Mephenytoin', 'Moderate', 'Chloramphenicol often markedly increases serum phenytoin levels. Toxicity has been reported. Also, phenytoin has been reported both to increase and to decrease serum chloramphenicol levels. Significant systemic absorption of otic or ophthalmic chloramphenicol products is unlikely. However, the clinician should be aware of the possibility of a drug interaction. Similar effects may occur with other hydantoins.', NULL, 'This combination usually should be avoided if possible.', NULL, 'This combination usually should be avoided if possible. Patients should be closely observed for signs of hydantoin toxicity if these drugs must be given concurrently and serum phenytoin concentrations should be monitored. Patients should be advised to notify their physician if they experience symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia. The hydantoin dose may need to be decreased.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/189976/', '[1] Saltiel M, Stephens NM "Phenytoin-chloramphenicol interaction." Drug Intell Clin Pharm 14 (1980): 221[2] Harper JM, Yost RL, Stewart RB, Ciezkowski J "Phenytoin-chloramphenicol interaction: a retrospective study." Drug Intell Clin Pharm 13 (1979): 425-9[3] Christensen LK, Skovsted L "Inhibition of drug metabolism by chloramphenicol." Lancet 2 (1969): 1397-9[4] Krasinski K, Kusmiesz H, Nelson JD "Pharmacologic interactions among chloramphenicol, phenytoin and phenobarbital." Pediatr Infect Dis 1 (1982): 232-5[5] Powell DA, Nahata MC, Durrell