INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (189778, 'Enoxacin', 'Chlorpromazine', 'Moderate', 'Coadministration with moderate or strong CYP450 1A2 inhibitors may increase the plasma concentrations and risk of adverse effects of chlorpromazine. The proposed mechanism is inhibition of CYP450 1A2-mediated metabolism of chlorpromazine, which has been shown to be metabolized by this isoenzyme. This interaction has been studied in vitro, but clinical data evaluating the interaction are not available.', NULL, 'Until more information is available, caution is advised if chlorpromazine is used concomitantly with moderate or strong CYP450 1A2 inhibitors.', NULL, 'Until more information is available, caution is advised if chlorpromazine is used concomitantly with moderate or strong CYP450 1A2 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring of chlorpromazine should be considered whenever a moderate or strong CYP450 1A2 inhibitor is added to or withdrawn from therapy. Patients should be advised to monitor for signs and symptoms of chlorpromazine-related adverse reactions.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190005/', '[1] Wojcikowski J, Boksa J, Daniel WA "Main contribution of the cytochrome P450 isoenzyme 1A2 (CYP1A2) to N-demethylation and 5-sulfoxidation of the phenothiazine neuroleptic chlorpromazine in human liver--A comparison with other phenothiazines." Biochem Pharmacol 80 (2010): 1252-9[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365209);
