INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (189927, 'Clotrimazole', 'Telaprevir', 'Moderate', 'Coadministration of an azole antifungal agent and hepatitis C virus (HCV) NS3/4A protease inhibitor (boceprevir, telaprevir) may result in increased plasma concentrations of both drugs. The mechanism may involve both competitive and noncompetitive inhibition of CYP450 3A4, since these drugs are all substrates as well as inhibitors of the isoenzyme. Clinically, high plasma levels of an azole antifungal agent may increase the risk of QT interval prolongation and torsade de pointes arrhythmia.', NULL, 'Caution is advised if azole antifungal agents are used in combination with HCV NS3/4A protease inhibitors.', NULL, 'Caution is advised if azole antifungal agents are used in combination with HCV NS3/4A protease inhibitors. Dosage adjustments may be required. According to the product information for boceprevir and telaprevir, high dosages of ketoconazole and itraconazole (>200 mg/day) should be avoided.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190154/', '[1] "Product Information. Victrelis (boceprevir)." Schering-Plough Corporation, Kenilworth, NJ.[2] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.[3] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767365209);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (189928, 'Clotrimazole', 'Terfenadine', 'Major', 'Coadministration with azole antifungal agents may significantly increase the plasma concentrations of terfenadine. The mechanism is azole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of terfenadine. High plasma levels of terfenadine have been associated with prolongation of the QT interval on the ECG; ventricular arrhythmias including ventricular tachycardia and torsade de pointes; cardiac arrest; and sudden death. Within the azole class, ketoconazole and itraconazole are considered the most potent inhibitors, while fluconazole is comparatively weak and generally causes clinically significant interactions with CYP450 3A4 substrates only at dosages of 200 mg/day or more.', NULL, 'The use of terfenadine with most azole antifungal agents, including fluconazole at multiple doses of 400 mg or higher, is considered contraindicated.', NULL, 'The use of terfenadine with most azole antifungal agents, including fluconazole at multiple doses of 400 mg or higher, is considered contraindicated. Some authorities consider concomitant administration of terfenadine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole (AU). Loratadine, cetirizine, or fexofenadine may be safer alternatives during therapy with azole antifungal agents.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190155/', '[1] Crane JK, Shih HT "Syncope and cardiac arrhythmia due to an interaction between itraconazole and terfenadine." Am J Med 95 (1993): 445-6[2] Woosley RL, Chen Y, Freiman JP, Gillis RA "Mechan