        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1437565, 'Isosorbide dinitrate', 'Trandolapril', 'Moderate', 'Angiotensin converting enzyme (ACE) inhibitors may enhance the vasodilatory and hypotensive effects of nitroglycerin. Data have also shown that captopril can prevent nitrate tolerance. ACE inhibitors can decrease systemic vascular resistance and cardiac work, further enhancing the effectiveness of nitroglycerin.', NULL, 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose.', NULL, 'In general, this combination is used to clinical advantage; however, some manufacturers recommend that nitrates and other vasodilators should be discontinued before starting ACE inhibitors or resumed at a reduced dose. Blood pressure monitoring is advisable.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
