INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437885, 'Triptorelin', 'Ozanimod', 'Major', 'Due to the risk of bradycardia and atrioventricular (AV) block, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of ozanimod treatment in patients receiving drugs that prolong the QT interval.', 'DDInter', 1767317476, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with ozanimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.', 'Synergism', 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
