INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437956, 'Ephedrine', 'Tromethamine', 'Moderate', 'Alkalization of the urine decreases the urinary excretion increases the elimination half-life of ephedrine, pseudoephedrine, and related drugs. Toxicity from long-term use of pseudoephedrine has been demonstrated in patients with persistently alkaline urine.', 'DDInter', 1767317476, 'Patients should be monitored for toxic effects such as tremor, anxiety, insomnia, irritability, or nervousness. Dosage reductions may be required.', 'Excretion', 'Patients should be monitored for toxic effects such as tremor, anxiety, insomnia, irritability, or nervousness.', NULL, NULL, 'Moderate', 'pharmacodynamic');
