INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437958, 'Flecainide', 'Tromethamine', 'Minor', 'Agents that alkalinize the urine decrease the renal elimination of flecainide. The result may be increased plasma flecainide levels and increased effectiveness. The patient should be monitored for flecainide toxicity, and plasma flecainide levels should be checked when appropriate. Also, flecainide effectiveness may be decreased when an agent that alkalinizes the urine is discontinued.', 'DDInter', 1767317476, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437959, 'Glipizide', 'Tromethamine', 'Minor', 'Alkalinization of the urine may increase the renal clearance of some sulfonylureas for which renal elimination is pH dependent. The clinical significance of this interaction is unknown. Management consists of monitoring blood glucose frequently during the initial period of coadministration. The sulfonylurea dosage should be adjusted as needed and appropriate.', 'DDInter', 1767317476, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
