INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437961, 'Tromethamine', 'Lisdexamfetamine', 'Moderate', 'Alkalinization of the urine can decrease the renal elimination of amphetamines. Amphetamine effects may be prolonged, and the risk of toxicity may be increased.', 'DDInter', 1767317476, 'Urinary alkalinizers should generally not be used with amphetamines, particularly in an amphetamine overdose situation.', 'Excretion', 'Urinary alkalinizers should generally not be used with amphetamines, particularly in an amphetamine overdose situation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437962, 'Tromethamine', 'Lithium carbonate', 'Moderate', 'Urinary alkalinizers may increase excretion of lithium. Plasma lithium levels and therapeutic effects may be decreased.', 'DDInter', 1767317476, 'Monitoring of lithium concentrations and therapeutic effect is recommended during concurrent use.', 'Others', 'Monitoring of lithium concentrations and therapeutic effect is recommended during concurrent use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437963, 'Tromethamine', 'Mecamylamine', 'Moderate', 'Alkalinization of the urine can decrease the renal elimination of mecamylamine. A more pronounced and prolonged antihypertensive action may result.', 'DDInter', 1767317476, 'Blood pressure should be closely monitored. It may be necessary to decrease mecamylamine dosage.', 'Excretion', 'Blood pressure should be closely monitored.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437964, 'Tromethamine', 'Metamfetamine', 'Moderate', 'Alkalinization of the urine can decrease the renal elimination of amphetamines. Amphetamine effects may be prolonged, and the risk of toxicity may be increased.', 'DDInter', 1767317476, 'Urinary alkalinizers should generally not be used with amphetamines, particularly in an amphetamine overdose situation.', 'Excretion', 'Urinary alkalinizers should generally not be used with amphetamines, particularly in an amphetamine overdose situation.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437965, 'Tromethamine', 'Oxytetracycline', 'Moderate', 'INTERVAL: Alkalinization of the urine may decrease the plasma concentration of some tetracyclines. The mechanism is not completely understood, but may involve increased renal elimination of tetracyclines when the urine becomes alkalinized.', 'DDInter', 1767317476, 'If these drugs must be used together, an alternating dosing schedule is recommended (three to four hours apart).', 'Excretion', 'If these drugs must be used together, an alternating dosing schedule is recommended (three to four hours apart).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1437966, 'Tromethamine', 'Quinidine', 'Moderate', 'Coadministration with drugs that can increase urinary pH such as antacids, carbonic anhydrase inhibitors, or urinary alkalinizers may decrease the urinary excretion of quinidine. The proposed mechanism is increased renal tubular reabsorption due to reduced ionization of quinidine in alkaline urine.', 'DDInter', 1767317476, 'Caution is advised if quinidine is used with agents that can increase urinary pH. Quinidine levels may need to be monitored more closely following addition or discontinuation of these agents, and the quinidine dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate quinidine toxicity such as tinnitus, hearing loss, visual disturbances, diarrhea, headache, dizziness, palpitations, syncope, or irregular heartbeats.', 'Excretion', 'Caution is advised if quinidine is used with agents that can increase urinary pH.', NULL, NULL, 'Moderate', 'pharmacodynamic');
