INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190276, 'Demeclocycline', 'Zinc chloride', 'Moderate', 'INTERVAL: The bioavailability of oral tetracyclines may be significantly decreased during concurrent administration with zinc-containing products. Therapeutic failure may result. The proposed mechanism is chelation of tetracyclines by the zinc cation, forming an insoluble complex that is poorly absorbed from the gastrointestinal tract.', NULL, 'Patients receiving an oral tetracycline in combination with zinc-containing products should be advised to separate the times of administration by at least three to four hours.', NULL, 'Patients receiving an oral tetracycline in combination with zinc-containing products should be advised to separate the times of administration by at least three to four hours. Doxycycline may be an appropriate alternative, since its absorption may not be significantly affected by zinc coadministration.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190503/', '[1] Neuvonen PJ "Interactions with the absorption of tetracyclines." Drugs 11 (1976): 45-54[2] "Product Information. Nuzyra (omadacycline)." Paratek Pharmaceuticals, Inc., Boston, MA.[3] "Product Information. Seysara (sarecycline)." Allergan Inc, Irvine, CA.[4] Gothoni G, Neuvonen PJ, Mattila M, Hackman R "Iron-tetracycline interaction: effect of time interval between the drugs." Acta Med Scand 191 (1972): 409-11[5] Penttila O, Hurme H, Neuvonen PJ "Effect of zinc sulphate on the absorption of tetracycline and doxycycline in man." Eur J Clin Pharmacol 9 (1975): 131-4[6] "Product Information. Achromycin (tetracycline)." Lederle Laboratories, Wayne, NJ.[