INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190302, 'Diflunisal', 'Alemtuzumab', 'Moderate', 'Coadministration of alemtuzumab with drugs that have antiplatelet or anticoagulant effects, such as nonsteroidal anti-inflammatory drugs (NSAIDs), may increase the risk of bleeding. Use of alemtuzumab alone has been associated with autoimmune and hemolytic anemia, thrombocytopenia, prolonged myelosuppression, bone marrow aplasia, hemophilia, and pancytopenia. The mechanism for this interaction has not been clearly delineated, but is presumably due to additive antiplatelet/anticoagulant effects.', NULL, 'Caution is recommended if alemtuzumab is used concomitantly with NSAIDS.', NULL, 'Caution is recommended if alemtuzumab is used concomitantly with NSAIDS. Patients should be monitored for increased anticoagulant effects and bleeding complications. Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190529/', '[1] "Product Information. Campath (alemtuzumab)" Berlex, Richmond, CA.[2] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190303, 'Aluminum hydroxide', 'Diflunisal', 'Minor', 'Concomitant administration of diflunisal and aluminum-containing products reduces the oral bioavailability of diflunisal by up to 40%. Doses of diflunisal and aluminum-containing products should be separated by at least 2 hours to ensure adequate absorption of diflunisal. Patients should be monitored for altered clinical effects of diflunisal.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190530/', '[1] "Product Information. Dolobid (diflunisal)." Merck & Co, Inc, West Point, PA.[2] Verbeeck R, Tjandramaga TB, Mullie A, Verbesselt R, de Schepper PJ "Effect of aluminum hydroxide on diflunisal absorption." Br J Clin Pharmacol 7 (1979): 519-22[3] D''Arcy PF, McElnay JC "Drug-antacid interactions: assessment of clinical importance." Drug Intell Clin Pharm 21 (1987): 607-17', 1767363027);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (190304, 'Anisindione', 'Diflunisal', 'Major', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the hypoprothrombinemic effect and bleeding risk associated with oral anticoagulants.', NULL, 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk.', NULL, 'NSAIDs should be administered with oral anticoagulants only if benefit outweighs risk. The INR should be checked frequently and oral anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of NSAIDs in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of unusual bleeding or bruising to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools. Salicylates (except aspirin) appear to have less effect on coagulation and may be preferable in patients treated with oral anticoagulants.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/190531/', '[1] Loftin JP, Vesell ES "Interaction between sulindac and warfarin: different results in normal subjects and in an unusual patient with a potassium-losing renal tubular defect." J Clin Pharmacol 19 (1979): 733-42[2] Gabb GM "Fatal outcome of interaction between warfarin and a non-steroidal anti-inflammatory drug." Med J Aust 164 (1996): 700-1[3] Penning-van Beest F, Erkens J, Petersen KU, Koelz HR, Herings R "Main comedications associated with major bleeding during anticoagulant ther